import React, { useState, useEffect } from 'react';
import { Button, Card, Modal } from 'semantic-ui-react';
import ProjectForm from './ProjectForm';
import EditProjectForm from './EditProjectForm';
import { UserContext } from '../../User';
import { useContext } from 'react';


function Projects({handleProjectDelete}) {
  const [projects, setProjects] = useState([]);
  const [editingProject, setEditingProject] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [deletingProject, setDeletingProject] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);
  const [showTasksModal, setShowTasksModal] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const {user, refreshUser} = useContext(UserContext)
  const [tasks, setTasks] = useState([]);
  const [showAddTaskModal, setShowAddTaskModal] = useState(false);
  const [showDeleteTaskModal, setShowDeleteTaskModal] = useState(false);
  const [deletingTask, setDeletingTask] = useState(null);
  const [editingTask, setEditingTask] = useState(null);
const [showEditTaskModal, setShowEditTaskModal] = useState(false);



  useEffect(() => {
    async function fetchData() {
      try {
        const sessionResponse = await fetch('/check_session');
        const sessionData = await sessionResponse.json();
        const userId = sessionData.id;
        const response = await fetch(`/projects?user_id=${userId}`);
        const data = await response.json();

        const projectsWithUserId = data.map(project => ({
          ...project,
          user_id: userId,
        }));
        setProjects(projectsWithUserId);
      } catch (error) {
        console.log(error);
      }
    }

    fetchData();
  }, []);

  const handleDelete = (projectId) => {
    console.log('Deleting project with ID:', projectId);
    fetch(`/projects/${projectId}`, { method: "DELETE" })
      .then(() => {
        refreshUser();
      })
      .catch((error) => {
        console.log(error);
      });
  };


  const handleEdit = (project) => {
    setEditingProject(project);
    setShowEditModal(true);
  };

  const handleDeleteModal = (project) => {
    setDeletingProject(project);
    setShowDeleteModal(true);
  };

  const handleUpdateProject = async (updatedProject) => {
    try {
      const response = await fetch(`/projects/${updatedProject.id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedProject),
      });

      if (!response.ok) {
        throw new Error('Failed to update project.');
      }

      const data = await response.json();
      setProjects((projects) =>
        projects.map((project) => (project.id === data.id ? data : project))
      );
      setEditingProject(null);
      setShowEditModal(false);
    } catch (error) {
    }
  };

  const handleTasksModal = (project) => {
    setSelectedProject(project);
    setShowTasksModal(true);
  };

  const handleTaskClick = (task) => {
    setSelectedTask(task);
  };

  const handleAddTask = async (newTask) => {
    try {
      const response = await fetch(`/tasks`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newTask),
      });

      if (!response.ok) {
        throw new Error('Failed to add task.');
      }

      const data = await response.json();
      setTasks([...tasks, data]);
      setShowAddTaskModal(false);
    } catch (error) {
      console.log(error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      const response = await fetch(`/tasks/${taskId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to delete task.');
      }

      setTasks((tasks) => tasks.filter((task) => task.id !== taskId));
      setShowDeleteTaskModal(false);
      setDeletingTask(null);
    } catch (error) {
      console.log(error);
    }
  };

  const handleEditTaskModal = (task) => {
    setSelectedTask(task);
    setShowEditTaskModal(true);
  };

const projectComponents = (user?.projects || []).map((eachProject) => {
    return (
      <Card key={eachProject.id}>
        <Card.Content>
          <Card.Header>{eachProject.name}</Card.Header>
          <Card.Meta>{eachProject.status}</Card.Meta>
          <Card.Description>{eachProject?.description}</Card.Description>
        </Card.Content>
        <Card.Content extra>
          <Button basic color='blue' onClick={() => handleEdit(eachProject)}>Manage</Button>
          <Button basic color='green' onClick={() => handleTasksModal(eachProject)}>Tasks</Button>
          <Button basic color='red' onClick={() => handleDeleteModal(eachProject)}>Delete</Button>
        </Card.Content>
      </Card>
    );
  });

  return (
    <>
      <div>
      <ProjectForm setProjects={setProjects} projects={projects} />
      </div>
      <div className="projects-container">{projectComponents}</div>
      <Modal
    open={showEditModal}
    onClose={() => {
      setShowEditModal(false);
      setEditingProject(null);
    }}
  >
    <Modal.Header>Manage Project</Modal.Header>
    <Modal.Content>
      <EditProjectForm
        project={editingProject}
        handleUpdateProject={handleUpdateProject}
      />
    </Modal.Content>
  </Modal>
  <Modal
  open={showDeleteModal}
  onClose={() => {
    setShowDeleteModal(false);
    setDeletingProject(null);
  }}
>
  <Modal.Header>Delete Project</Modal.Header>
  <Modal.Content>
    <p>Are you sure you want to delete {deletingProject?.name}?</p>
  </Modal.Content>
  <Modal.Actions>
    <Button
      basic
      color="red"
      onClick={() => {
        handleDelete(deletingProject.id);
        setShowDeleteModal(false);
        setDeletingProject(null);
        handleProjectDelete = {handleProjectDelete}

      }}
    >
      Delete
    </Button>
    <Button basic color="blue" onClick={() => setShowDeleteModal(false)}>
      Cancel
    </Button>
  </Modal.Actions>
</Modal>
<Modal
      open={showTasksModal}
      onClose={() => {
        setShowTasksModal(false);
        setSelectedProject(null);
      }}
    >
      <Modal.Header>{selectedProject?.name} Tasks</Modal.Header>
      <Modal.Content>
        <div className="task-list">
          {tasks.map((task) => (
            <div
              key={task.id}
              className="task-item"
              onClick={() => handleEditTaskModal(task)}
            >
              <div>{task.title}</div>
              <div>{task.status}</div>
            </div>
          ))}
        </div>
        <Button onClick={handleAddTaskModal}>Add Task</Button>
      </Modal.Content>
    </Modal>
    <Modal
      open={showAddTaskModal}
      onClose={() => {
        setShowAddTaskModal(false);
      }}
    >
      <Modal.Header>Add Task</Modal.Header>
      <Modal.Content>
        {/* render form for adding task */}
      </Modal.Content>
    </Modal>
    <Modal
      open={showEditTaskModal}
      onClose={() => {
        setShowEditTaskModal(false);
        setSelectedTask(null);
      }}
    >
      <Modal.Header>Edit Task</Modal.Header>
      <Modal.Content>
        {/* render form for editing task */}
      </Modal.Content>
    </Modal>
</>
);
          }

export default Projects;
