import React, {useState} from 'react'
import {Route} from 'react-router-dom'
import App from '../../App'


function SignUp({handleSignupClick}) {

    const [session, setSession] = useState(false)
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [image, setImage] = useState("")
    const [email, setEmail] = useState("")

    if (session === false){

    const user = {
      username: username,
      password: password,
      image: image
    }

    const handleSubmit = (e) => {
      e.preventDefault()
      fetch("http://localhost:4000/signup", {
        method: "POST",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify(user)
      })
      .then(r => {
        if (r.ok){
          r.json()
          .then( console.log('success'))
          setSession(!session)
        } else {
          console.log('failure')
        }
      })
        e.target.reset()
    }

    const setSignupState = (e) => {
        handleSignupClick(e)
    }

    return (
      <div>
      <div>Welcome to Proflow!</div>
      <div>
        <div>
          <h2>Create a New Account</h2>
        </div>
        <form onSubmit={handleSubmit}>
          <div>
            <input onChange={(e) => setUsername(e.target.value)} type="text" name="username" placeholder="username" />
          </div>
          <div>
            <input onChange={(e) => setPassword(e.target.value)} type="text" name="password" placeholder="password" />
            <div>
            <input onChange={(e) => setEmail(e.target.value)} type="text" name="image" placeholder="profile photo URL" />
          </div>
          </div>
          <div>
            <input onChange={(e) => setImage(e.target.value)} type="text" name="image" placeholder="profile photo URL" />
          </div>
            <button >Submit</button>
        </form>
        <div class='ui basic buttons'>
            <button class="ui button" onClick={setSignupState}>Return to Login</button>
        </div>
      </div>
      </div>
    );
    }
    else {
        return (
        <main>
            <Route path="/">
                <App/>
            </Route>
        </main>
        )
    }
}

export default SignUp;
