import React, { useEffect, useState } from 'react';

const UserContext = React.createContext();

function UserProvider({children}) {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch("/check_session").then(r => {
      if (r.ok) {
        r.json().then((user) => setUser(user));
      }
    });
  }, []);

  const deleteUserProject = (id) => {
    const userCopy = {...user}
    userCopy.projects = userCopy.projects.filter(project => {
        return project.id !== id
    })
    setUser(userCopy)
}

const refreshUser = () => {
  fetch("/check_session").then(r => {
      if (r.ok) {
          r.json().then((user) => setUser(user));
      }
  });
}
  const refreshUserProjects = () => {
    fetch("/projects").then(r => {
        if (r.ok) {
            r.json().then((user) => setUser(user));
        }
    });
}

return(
  <UserContext.Provider value = {{user, setUser, refreshUserProjects,refreshUser , deleteUserProject}}>
    {children}
  </UserContext.Provider>
)
}

export {UserContext, UserProvider}
