          method: 'DELETE',
        })
          .then(response => {
            if (response.ok) {
              window.location.reload(); // refresh page to update the list of projects
            }
          })
          .catch(error => console.log(error));
      };

  return (
    <div>
      <h2>{name}</h2>
      <p>{description}</p>
      <p>Start Date: {start_date}</p>
      <p>End Date: {end_date}</p>
      <p>Status: {status}</p>
      <p>User ID: {user_id}</p>
    </div>
  );
}

