import { useContext } from 'react';
import { MyContext } from '../../MyContext';

function MyAccount() {
  const user = useContext(MyContext);

  if (!user) {
    return <Main to="/login" />;
  }

  return (
    <div>
      <h1>{user.username}</h1>
      <p>{user.email}</p>
      <img src={user.image} alt='Profile Pic'/>
    </div>
  );
}

export default MyAccount;
