import React, { useState, useEffect } from "react";
import { Routes, Route } from "react-router";
import Sidebar from './components/Sidebar'
import Projects from "./components/Projects/Projects";
import MyAccount from "./components/MyAccount/MyAccount"
import Home from "./components/Home";
import SignUp from './components/Logging/SignUp'
import LogIn from './components/Logging/Login'
import { UserContext, UserProvider } from "./User";

function App() {
    const [signUp, setSignUp] = useState(false)
    const handleSignupClick=() =>{
        setSignUp(!signUp)
    }

    const [user, setUser] = useState(null); // add user state

    const handleLogin = (loggedInUser) => {
        setUser(loggedInUser);
    };

    const handleLogout = () => {
        setUser(null);
    };

    const [projects, setProjects] = useState([])

    useEffect(() => {
        fetch('http://127.0.0.1:5555/projects')
        .then(r => r.json())
        .then(data => setProjects(data))
    }, [])


    const addProjectsToState = (newProjectObj) => {
        setProjects([newProjectObj, ...projects])
    }

    const showRemainingProjects = (id) => {
        const newProjectArray = projects.filter(projectObj => projectObj.id !== id)
        setProjects(newProjectArray)
    }

    return (

        <div >
          <UserProvider>            
            {signUp ? (
                <div>
                    <SignUp handleSignupClick = {handleSignupClick}/>
                </div>
            ):(
                <div>
                    <LogIn handleSignupClick = {handleSignupClick} handleLogin={handleLogin} /> {/* pass handleLogin down as a prop */}
                </div>
            )}
            <div className='pageContainer'>
                {user && <Sidebar handleLogout={handleLogout} />} {/* render Sidebar only when user is truthy */}
                <div>
                    <Routes>
                        <Route path="/" element={<Home />} />
                        <Route path="/projects" element={<Projects addProjectsToState={addProjectsToState} showRemainingProjects={showRemainingProjects} />} />
                        <Route path="/myaccount/*" element={<MyAccount />}/>
                    </Routes>
                </div>
            </div> </UserProvider>

        </div>

    )}



export default App
