import React, { useState, useEffect, useContext } from "react";
import { Routes, Route } from "react-router";
import Sidebar from './components/Sidebar'
import Projects from "./components/Projects/Projects";
import MyAccount from "./components/MyAccount/MyAccount"
import Home from "./components/Home";
import Tasks from "./components/Tasks/Tasks";
import { MyContext } from './MyContext';
import { MyContextProvider } from "./MyContext";

function Main() {
  const [user, setUser] = useState(null);
  const [projects, setProjects] = useState([]);
  const { updateUser } = useContext(MyContext);

  useEffect(() => {
    fetch('http://127.0.0.1:5555/projects')
      .then((r) => r.json())
      .then((data) => setProjects(data));
  }, []);

  const addProjectsToState = (newProjectObj) => {
    setProjects([newProjectObj, ...projects]);
  };

  const showRemainingProjects = (id) => {
    const newProjectArray = projects.filter(
      (projectObj) => projectObj.id !== id
    );
    setProjects(newProjectArray);
  };

  const handleLogin = async (username, password) => {
    // Make API call to authenticate user
    const response = await fetch('http://localhost:4000//login', {
      method: 'POST',
      body: JSON.stringify({ username, password }),
      headers: {
        'Content-Type': 'application/json',
      },
    });

    // If login is successful, get user data and update context
    if (response.ok) {
      const userData = await response.json();
      updateUser(userData);
      setUser(userData); // Add this line to update the user state
    }
  };

  return (
    <MyContextProvider value={{ user }}> {/* Add user state to the value */}
      <div className="pageContainer">
        <Sidebar />
        <div>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route
              path="/projects"
              element={
                <Projects
                  addProjectsToState={addProjectsToState}
                  showRemainingProjects={showRemainingProjects}
                />
              }
            />
            <Route path="/myaccount" element={<MyAccount />} />
          </Routes>
        </div>
      </div>
    </MyContextProvider>
  );
}

export default Main;
