  return (
    <div>
      <h2>{name}</h2>
      <p>{description}</p>
      <p>Start Date: {start_date}</p>
      <p>End Date: {end_date}</p>
      <p>Status: {status}</p>
      <p>User ID: {user_id}</p>
      <button onClick={handleEdit}>Edit</button>
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
}

