import React, { useState, useEffect } from 'react';
import ProjectCard from './ProjectCard';
import ProjectForm from './ProjectForm';
import EditProjectForm from './EditProjectForm';

function Projects() {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    fetch('/projects')
      .then(response => response.json())
      .then(data => setProjects(data))
      .catch(error => console.log(error));
  }, []);

  const handleUpdate = (updatedProject) => {
    const updatedProjects = projects.map((project) => {
      if (project.id === updatedProject.id) {
        return updatedProject;
      } else {
        return project;
      }
    });
    setProjects(updatedProjects);
  };

  const handleDelete = (deletedProjectId) => {
    const updatedProjects = projects.filter((project) => project.id !== deletedProjectId);
    setProjects(updatedProjects);
  };

  const projectComponents = projects.map((eachProject) => {
    return (
      <ProjectCard 
        key={eachProject.id}
        project={eachProject}
        onUpdate={handleUpdate}
        onDelete={handleDelete}
      />
    )
  })

  return (
    <><div><ProjectForm /></div><div className="projects-container">
      {projectComponents}
    </div></>
  );
}
