import React, { useState } from 'react';

function EditProjectForm({ id, name, description, start_date, end_date, status, user_id, onCancel, onUpdate }) {
  const [newName, setNewName] = useState(name);
  const [newDescription, setNewDescription] = useState(description);
  const [newStartDate, setNewStartDate] = useState(start_date);
  const [newEndDate, setNewEndDate] = useState(end_date);
  const [newStatus, setNewStatus] = useState(status);

  const handleNameChange = (event) => setNewName(event.target.value);
  const handleDescriptionChange = (event) => setNewDescription(event.target.value);
  const handleStartDateChange = (event) => setNewStartDate(event.target.value);
  const handleEndDateChange = (event) => setNewEndDate(event.target.value);
  const handleStatusChange = (event) => setNewStatus(event.target.value);

  const handleSubmit = (event) => {
    event.preventDefault();
    const updatedProject = {
      id: id,
      name: newName,
      description: newDescription,
      start_date: newStartDate,
      end_date: newEndDate,
      status: newStatus,
      user_id: user_id,
    };

    fetch(`/projects/${id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedProject),
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Failed to update project.');
        }
      })
      .then((updatedProject) => {
        onUpdate(updatedProject);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="name">Name:</label>
      <input type="text" id="name" value={newName} onChange={handleNameChange} />

      <label htmlFor="description">Description:</label>
      <input type="text" id="description" value={newDescription} onChange={handleDescriptionChange} />

      <label htmlFor="start_date">Start Date:</label>
      <input type="text" id="start_date" value={newStartDate} onChange={handleStartDateChange} />

      <label htmlFor="end_date">End Date:</label>
      <input type="text" id="end_date" value={newEndDate} onChange={handleEndDateChange} />

      <label htmlFor="status">Status:</label>
      <input type="text" id="status" value={newStatus} onChange={handleStatusChange} />

      <button type="submit">Update Project</button>
      <button type="button" onClick={onCancel}>
        Cancel
      </button>
    </form>
  );
}

export default EditProjectForm;
