import React, { useState, useEffect } from 'react';
import ProjectCard from './ProjectCard';
import ProjectForm from './ProjectForm';
import EditProjectForm from './EditProjectForm';

function Projects() {
  const [projects, setProjects] = useState([]);
  const [editingProjectId, setEditingProjectId] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5555/projects/')
      .then(response => response.json())
      .then(data => setProjects(data))
      .catch(error => console.log(error));
  }, []);



  const handleAdd = (newProject) => {
    setProjects([...projects, newProject]);
  }

  const handleEdit = (updatedProject) => {
    fetch(`/projects/${updatedProject.id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(updatedProject)
    })
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Network response was not ok.');
        }
      })
      .then(data => {
        const updatedProjects = projects.map(project => {
          if (project.id === data.id) {
            return data;
          } else {
            return project;
          }
        });
        setProjects(updatedProjects);
        setEditingProjectId(null);
      })
      .catch(error => console.log(error));
  }

  const handleDelete = (id) => {
    const updatedProjects = projects.filter(project => project.id !== id);
    setProjects(updatedProjects);
  }

  const handleEditClick = (id) => {
    setEditingProjectId(id);
  }

  const handleCancelEdit = () => {
    setEditingProjectId(null);
  }

  const projectComponents = projects.map((eachProject) => {
    if (editingProjectId === eachProject.id) {
      return (
        <EditProjectForm
          key={eachProject.id}
          project={eachProject}
          onEdit={handleEdit}
          onCancelEdit={handleCancelEdit}
        />
      )
    } else {
      return (
        <ProjectCard
          key={eachProject.id}
          project={eachProject}
          onDelete={handleDelete}
          onEditClick={handleEditClick}
        />
      )
    }
  });

  return (
    <>
      <div><ProjectForm onAdd={handleAdd} /></div>
      <div className="projects-container">
        {projectComponents}
      </div>
    </>
  );
}

export default Projects;
