import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Main from '../Main';

const sessionUser = [];

function LogIn({ handleSignupClick }) {
  const navigate = useNavigate();
  const [session, setSession] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newUser = {
      username,
      password,
    };
    sessionUser.push(newUser);
    fetch('/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newUser),
    }).then((r) => {
      if (r.status === 200) {
        r.json();
        setSession(!session);
        navigate('/');
      } else {
        console.log('failure');
      }
    });
    e.target.reset();
  };

  const setSignupState = (e) => {
    handleSignupClick(e);
  };

  return (
    <div>
      <div className="ui center aligned huge header" style={{ margin: 40 }}>
        Welcome to SmartPark!
      </div>
      {!session ? (
        <div className="new-user-form">
          <div className="ui sizer vertical segment">
            <h2 className="ui center aligned large header">Login Here</h2>
          </div>
          <form className="ui form" onSubmit={handleSubmit}>
            <div className="field">
              <input
                onChange={(e) => setUsername(e.target.value)}
                type="text"
                name="username"
                placeholder="username"
              />
            </div>
            <div className="field">
              <input
                onChange={(e) => setPassword(e.target.value)}
                type="password"
                name="password"
                placeholder="password"
              />
            </div>
            <button className="fluid ui button" type="submit">
              Submit
            </button>
          </form>
          <div className="ui basic buttons">
            <button onClick={setSignupState} className="ui button">
              Don't have an accout? Create one!
            </button>
          </div>
        </div>
      ) : (
        <main>
          <Route path="/">
            <Main currentUser={sessionUser[sessionUser.length - 1]} />
          </Route>
        </main>
      )}
    </div>
  );
}

export default LogIn;
