import React, { useState, useEffect } from 'react';
import ProjectCard from './ProjectCard';
import ProjectForm from './ProjectForm';
import EditProjectForm from './EditProjectForm';

function Projects() {
  const [projects, setProjects] = useState([]);
  const [editingProject, setEditingProject] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('/projects');
        const data = await response.json();
        setProjects(data);
      } catch (error) {
        console.log(error);
      }
    }

    fetchData();
  }, []);

  const handleDelete = async (id) => {
    try {
      await fetch(`/projects/${id}`, { method: 'DELETE' });
      const updatedProjects = projects.filter((project) => project.id !== id);
      setProjects(updatedProjects);
    } catch (error) {
      console.log(error);
    }
  };

  const handleEdit = (project) => {
    setEditingProject(project);
  };

  const handleUpdateProject = async (updatedProject) => {
    try {
      const response = await fetch(`/projects/${updatedProject.id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedProject),
      });

      if (!response.ok) {
        throw new Error('Failed to update project.');
      }

      const data = await response.json();
      setProjects((projects) =>
        projects.map((project) => (project.id === data.id ? data : project))
      );
      setEditingProject(null);
    } catch (error) {
      console.log(error);
    }
  };

  const projectComponents = projects.map((eachProject) => {
    return (
      <ProjectCard
        key={eachProject.id}
        handleDelete={handleDelete}
        handleEdit={handleEdit}
        {...eachProject}
      />
    );
  });

  return (
    <>
      <div>
        <ProjectForm />
      </div>
      <div className="projects-container">{projectComponents}</div>
      {editingProject && (
        <EditProjectForm
          project={editingProject}
          onClose={() => setEditingProject(null)}
          handleUpdateProject={handleUpdateProject}
        />
      )}
    </>
  );
}

export default Projects;
