import React, { useState } from 'react';
import EditProjectForm from './EditProjectForm';

function ProjectCard({ project, onUpdate, onDelete }) {
  const [showEditForm, setShowEditForm] = useState(false);
  const [projectInfo, setProjectInfo] = useState({ id, name, description, start_date, end_date, status, user_id });

  const handleDelete = () => {
    fetch(`/projects/${id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (response.ok) {
          onDelete(id);
        } else {
          throw new Error('Failed to delete project.');
        }
      })
      .catch((error) => {
        console.log(error);
      });
    }
  const handleEditClick = () => {
    setShowEditForm(true);
  };

  const handleEditCancel = () => {
    setShowEditForm(false);
  };

  const handleUpdateProject = (updatedProject) => {
    fetch(`/projects/${id}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedProject),
    })
      .then((response) => {
        if (response.ok) {
          response.json().then((data) => {
            setProjectInfo(data);
            setShowEditForm(false);
            updatedProjects((prevProjects) =>
              prevProjects.map((project) =>
                project.id === data.id ? data : project
              )
            );
          });
        }
      })
      .catch((error) => console.log(error));
  };
  return (
    <div>
    <h2>{projectInfo.name}</h2>
    <p>{projectInfo.description}</p>
    <p>Start Date: {projectInfo.start_date}</p>
    <p>End Date: {projectInfo.end_date}</p>
    <p>Status: {projectInfo.status}</p>
    <p>User ID: {projectInfo.user_id}</p>
    <button onClick={handleEditClick}>Edit</button>
    <button onClick={handleDelete}>Delete</button>
      {showEditForm && (
      <EditProjectForm
        id={projectInfo.id}
        name={projectInfo.name}
        description={projectInfo.description}
        start_date={projectInfo.start_date}
        end_date={projectInfo.end_date}
        status={projectInfo.status}
        user_id={projectInfo.user_id}
        onUpdate={handleUpdateProject}
        onCancel={handleEditCancel}
      />
    )}
  </div>
);
}

export default ProjectCard;
