import React, { useState, useEffect } from 'react';
import ProjectCard from './ProjectCard';
import ProjectForm from './ProjectForm';

function Projects() {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    fetch('/projects')
      .then(response => response.json())
      .then(data => setProjects(data))
      .catch(error => console.log(error));
  }, []);

  const projectComponents = projects.map((eachProject) => {
    return (
      <ProjectCard 
        key={eachProject.id}
        {...eachProject}
      />
    )
  })

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`/projects/${id}`, { method: 'DELETE' });
      if (response.ok) {
        // Remove the deleted project from the list of projects in state
        setProjects((prevProjects) => prevProjects.filter((project) => project.id !== id));
      } else {
        throw new Error('Failed to delete project.');
      }
    } catch (error) {
      console.error(error);
    }
  };


  return (
    <><div><ProjectForm /></div><div className="projects-container">
      {projectComponents}
    </div></>
  );
}

export default Projects;
