import React, { useState } from 'react';
import EditProjectForm from './EditProjectForm';

function ProjectCard({
  id,
  name,
  description,
  start_date,
  end_date,
  status,
  user_id,
  showRemainingProjects,
  updatedProjects,
}) {
  const [showEditForm, setShowEditForm] = useState(false);
  const [projectInfo, setProjectInfo] = useState({ id, name, description, start_date, end_date, status, user_id });

  const handleDelete = () => {
    fetch(`/projects/${id}`, {
      method: 'DELETE',
    }).then(showRemainingProjects(id));
  };

  const handleEditClick = () => {
    setShowEditForm(true);
  };

  const handleEditCancel = () => {
    setShowEditForm(false);
  };

  const handleUpdateProject = (updatedProject) => {
    fetch(`/projects/${id}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedProject),
    })
      .then((response) => {
        if (response.ok) {
          response.json().then((data) => {
            setProjectInfo(data);
            setShowEditForm(false);
          });
        }
      })
      .catch((error) => console.log(error));
  };
  return (
    <div>
    <h2>{projectInfo.name}</h2>
    <p>{projectInfo.description}</p>
    <p>Start Date: {projectInfo.start_date}</p>
    <p>End Date: {projectInfo.end_date}</p>
    <p>Status: {projectInfo.status}</p>
    <p>User ID: {projectInfo.user_id}</p>
    <button onClick={handleEditClick}>Edit</button>
    <button onClick={handleDelete}>Delete</button>
      {showEditForm && (
        <EditProjectForm
          id={id}
          name={name}
          description={description}
          start_date={start_date}
          end_date={end_date}
          status={status}
          user_id={user_id}
          onUpdate={handleUpdateProject}
          onCancel={handleEditCancel}
        />
      )}
    </div>
  );
}

export default ProjectCard;
