import React, {useState} from 'react';
import EditProjectForm from './EditProjectForm';

function ProjectCard({ id, name, description, start_date, end_date, status, user_id, showRemainingProjects, updatedProjects }) {
    // const [projectAttr, setProjectAttr] = useState('')
    // const [newProjectInfo, setNewProjectInfo] = useState('')
    // const handleAttributeChange = e => setProjectAttr(e.target.value)
    // const handleNewProjectInfo = e => setNewProjectInfo(e.target.value)
    const [showEditForm, setShowEditForm] = useState(false);

    const handleDelete = () => {
        fetch(`/projects/${id}`, {
          method: 'DELETE',
        })
        .then(showRemainingProjects(id))
      };

    //   const handleEdit = (e) => {
    //     e.preventDefault()
    //     fetch (`/dogs/${id}`, {
    //         method:"PATCH", 
    //         headers:{"Content-type":"application/json"}, 
    //         body: JSON.stringify({
    //             [projectAttr]: newProjectInfo,
    //         })
    //         })
    //     .then (r => r.json())
    //     .then(editedProject => updatedProjects(editedProject))

    //     setNewProjectInfo('')
    //     setProjectAttr('')
    // }
    const handleEditClick = () => {
        setShowEditForm(true);
      };

      const handleEditCancel = () => {
        setShowEditForm(false);
      };


    return (
        <div>
          <h2>{name}</h2>
          <p>{description}</p>
          <p>Start Date: {start_date}</p>
          <p>End Date: {end_date}</p>
          <p>Status: {status}</p>
          <p>User ID: {user_id}</p>
          <button onClick={handleEditClick}>Edit</button>
          <button onClick={handleDelete}>Delete</button>
          {showEditForm && (
            <EditProjectForm
              id={id}
              name={name}
              description={description}
              start_date={start_date}
              end_date={end_date}
              status={status}
              user_id={user_id}
              onCancel={handleEditCancel}
            />
          )}
        </div>
      );
    }

export default ProjectCard;
