import React, { useState } from 'react';
import { Button, Card, Modal } from 'semantic-ui-react';
import TaskForm from './TaskForm';

function TaskModal({ projects, onClose }) {
  const [tasks, setTasks] = useState(projects.tasks || []);
  const [editingTask, setEditingTask] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [deletingTask, setDeletingTask] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);

  const handleAddTask = (task) => {
    setTasks((tasks) => [...tasks, task]);
  };

  const handleDeleteTask = (taskId) => {
    setTasks((tasks) => tasks.filter((task) => task.id !== taskId));
  };

  const handleEditTask = (task) => {
    setEditingTask(task);
    setShowEditModal(true);
  };

  const handleUpdateTask = (updatedTask) => {
    setTasks((tasks) =>
      tasks.map((task) => (task.id === updatedTask.id ? updatedTask : task))
    );
    setEditingTask(null);
    setShowEditModal(false);
  };

  const handleDeleteModal = (task) => {
    setDeletingTask(task);
    setShowDeleteModal(true);
  };

  const handleDelete = () => {
    const taskId = deletingTask.id;
    fetch(`/tasks/${taskId}`, { method: "DELETE" })
      .then(() => {
        handleDeleteTask(taskId);
        setShowDeleteModal(false);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const taskComponents = tasks.map((task) => {
    return (
      <Card key={task.id}>
        <Card.Content>
          <Card.Header>{task.name}</Card.Header>
          <Card.Meta>{task.status}</Card.Meta>
          <Card.Description>{task.description}</Card.Description>
        </Card.Content>
        <Card.Content extra>
          <Button basic color="blue" onClick={() => handleEditTask(task)}>
            Edit
          </Button>
          <Button basic color="red" onClick={() => handleDeleteModal(task)}>
            Delete
          </Button>
        </Card.Content>
      </Card>
    );
  });

  return (
    <Modal open={true} onClose={onClose}>
      <Modal.Header>{projects.name} Tasks</Modal.Header>
      <Modal.Content>
        {taskComponents.length === 0 ? (
          <p>No tasks assigned to this project.</p>
        ) : (
          <Card.Group>{taskComponents}</Card.Group>
        )}
      </Modal.Content>
      <Modal.Actions>
        <Button basic color="green" onClick={() => setShowEditModal(true)}>
          Add Task
        </Button>
        <Button basic color="blue" onClick={onClose}>
          Close
        </Button>
      </Modal.Actions>
      <Modal
        open={showEditModal}
        onClose={() => {
          setShowEditModal(false);
          setEditingTask(null);
        }}
      >
        <Modal.Header>Add/Edit Task</Modal.Header>
        <Modal.Content>
          <TaskForm
            project={project}
            task={editingTask}
            handleAddTask={handleAddTask}
            handleUpdateTask={handleUpdateTask}
            onClose={() => setShowEditModal(false)}
          />
        </Modal.Content>
      </Modal>
    </Modal>
  );
}

export default TaskModal;
