import React, { useEffect, useState } from 'react';
import { Button, Card, Modal } from 'semantic-ui-react';

function Tasks({ projectId, tasks, setTasks }) {
  const [showAddTaskModal, setShowAddTaskModal] = useState(false);
  const [showDeleteTaskModal, setShowDeleteTaskModal] = useState(false);
  const [deletingTask, setDeletingTask] = useState(null);
  const [editingTask, setEditingTask] = useState(null);

  const handleTaskAdd = async (task) => {
    try {
      const response = await fetch(`/projects/${projectId}/tasks`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(task),
      });

      if (!response.ok) {
        throw new Error('Failed to add task.');
      }

      const data = await response.json();
      setTasks([...tasks, data]);
      setShowAddTaskModal(false);
    } catch (error) {
    }
  };

  const handleTaskDelete = async (taskId) => {
    try {
      const response = await fetch(`/projects/${projectId}/tasks/${taskId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to delete task.');
      }

      setTasks(tasks.filter((task) => task.id !== taskId));
      setShowDeleteTaskModal(false);
      setDeletingTask(null);
    } catch (error) {
    }
  };
  return (
    <><Modal
      open={showAddTaskModal}
      onClose={() => setShowAddTaskModal(false)}
    >
      <Modal.Header>Add Task</Modal.Header>
      <Modal.Content>
        <TaskForm onSubmit={handleTaskAdd} />
      </Modal.Content>
    </Modal><Modal
      open={showDeleteTaskModal}
      onClose={() => {
        setShowDeleteTaskModal(false);
        setDeletingTask(null);
      } }
    >
        <Modal.Header>Delete Task</Modal.Header>
        <Modal.Content>
          <p>Are you sure you want to delete {deletingTask?.name}?</p>
        </Modal.Content>
        <Modal.Actions>
          <Button
            basic
            color="red"
            onClick={() => {
              handleTaskDelete(deletingTask.id);
            } }
          >
            Delete
          </Button>
          <Button basic color="blue" onClick={() => setShowDeleteTaskModal(false)}>
            Cancel
          </Button>
        </Modal.Actions>
      </Modal></>
    // ...
  );
}

export default Tasks;
