import React, {useState} from 'react';

function ProjectCard({ id, name, description, start_date, end_date, status, user_id, showRemainingProjects, updatedProjects }) {
    const [projectAttr, setProjectAttr] = useState('')
    const [newProjectInfo, setNewProjectInfo] = useState('')
    const handleAttributeChange = e => setProjectAttr(e.target.value)
    const handleNewProjectInfo = e => setNewProjectInfo(e.target.value)

    const handleDelete = () => {
        fetch(`/projects/${id}`, {
          method: 'DELETE',
        })
        .then(showRemainingProjects(id))
      };

      const handleEdit = (e) => {
        e.preventDefault()
        fetch (`/dogs/${id}`, {
            method:"PATCH", 
            headers:{"Content-type":"application/json"}, 
            body: JSON.stringify({
                [projectAttr]: newProjectInfo,
            })
            })
        .then (r => r.json())
        .then(editedProject => updatedProjects(editedProject))

        setNewProjectInfo('')
        setProjectAttr('')
    }


    return (
        <div>
            <h2>{name}</h2>
            <p>{description}</p>
            <p>Start Date: {start_date}</p>
            <p>End Date: {end_date}</p>
            <p>Status: {status}</p>
            <p>User ID: {user_id}</p>

            <form onSubmit={handleEdit}>
                <label htmlFor="projectAttr">Attribute to Edit:</label>
                <input type="text" id="projectAttr" name="projectAttr" value={projectAttr} onChange={handleAttributeChange} />

                <label htmlFor="newProjectInfo">New Project Info:</label>
                <input type="text" id="newProjectInfo" name="newProjectInfo" value={newProjectInfo} onChange={handleNewProjectInfo} />

                <button type="submit">Save Changes</button>
            </form>

            <button onClick={handleDelete}>Delete</button>
        </div>
    );
}

export default ProjectCard;
